"use strict";document.getElementById("currentYear").textContent=(new Date).getFullYear();const form=document.getElementById("dnsForm"),domainInput=document.getElementById("domainInput"),resultsEl=document.getElementById("results");form.addEventListener("submit",async t=>{t.preventDefault();const e=domainInput.value.trim();if(e){resultsEl.textContent="DNS records worden gecontroleerd...",resultsEl.style.opacity="0.7";try{const t=await fetch(`/dns-check?domain=${encodeURIComponent(e)}`);if(!t.ok){const e=await t.json().catch(()=>({}));return resultsEl.textContent=`Fout: ${e.error||t.statusText}`,void(resultsEl.style.opacity="1")}const n=await t.json();resultsEl.textContent=JSON.stringify(n,null,2),resultsEl.style.opacity="1"}catch(t){resultsEl.textContent=`Verzoek mislukt: ${t.message}`,resultsEl.style.opacity="1"}}});